import pytestfrom selenium.webdriver.remote.webdriver import WebDriverfrom selenium.webdriver.remote.webelement import WebElementclass BasePage:    def __init__(self, driver=WebDriver):        self.driver = driver    def findElementBy(self, locator: tuple) -> WebElement:        return self.driver.find_element(*locator)    def sendkeys(self, locator: tuple, value: str):        return self.findElementBy(*locator).send_keys(value)    def click(self, locator):        return self.findElementBy(*locator).click()    def driverGet(self, url: str):        self.driver.get(url)    def verifyLogin(self, expectedTitle):        title = self.driver.title        if expectedTitle in title:            print(f"logged in successfully with title {title}")        else:            pytest.fail(f"failed to login expected title is {title}")